// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;
import "./lib/Conversion.sol";

error FundMe__NotOwner();

contract FundMe {
    using Conversion for uint256;

    uint256 constant MIN_USD_AMOUNT = 50;
    address[] public funders;
    address public immutable i_owner;
    mapping(address => uint256) fundersToAmount;

    AggregatorV3Interface public priceFeed;

    constructor(address priceFeedAddress) {
        i_owner = msg.sender;
        priceFeed = AggregatorV3Interface(priceFeedAddress);
    }

    modifier onlyOwner() {
        // require(msg.sender == owner, "You are not the owner");
        if (msg.sender != i_owner) revert FundMe__NotOwner(); // new gas optimized way
        _;
    }

    // Fallbacks
    receive() external payable {
        fund();
    }

    fallback() external payable {
        fund();
    }

    function fund() public payable {
        //1. get the conversion rate usd/eth
        //2. check if it meets the min usd val
        require(
            msg.value.getConversionRate(priceFeed) >= MIN_USD_AMOUNT,
            "Did not send enought Ether"
        );
        funders.push(msg.sender);
        fundersToAmount[msg.sender] = msg.value;
    }

    //Function to withdraw funds from the contract and reset the mapping of funders
    function withdraw() public onlyOwner {
        //1. reset mapping of fundersToAmount
        for (uint256 i = 0; i < funders.length; i++) {
            address funder = funders[i];
            fundersToAmount[funder] = 0;
        }

        //2. reset the funders array
        funders = new address[](0);

        //3. Withdraw the contract funds to the owner
        (bool callSuccess, ) = payable(msg.sender).call{
            value: address(this).balance
        }("");
        require(callSuccess, "Transfer failed");
    }
}

